function setupButtons(){$(document).on("click",".btnWakeUp",function(){game.night=!1;refreshView()});$(document).on("click",".resetGame",function(){confirm("Are you sure you want to reset the game?")&&deleteGame()})}function refreshView(){game.startGame?$(".btnWakeUp").removeClass("hidden").show():$(".btnWakeUp").hide();game.night?$("").addClass("night"):$(".night").removeClass("night")}function saveGame(){$(".saveIcon").show();window.localStorage.setItem("game",JSON.stringify(game));game=JSON.parse(window.localStorage.getItem("game"));$(".saveIcon").hide()}function loadGame(){var n=JSON.parse(window.localStorage.getItem("game"));game=n!==null?n:getDefaultGame();console.info(game)}function deleteGame(){window.localStorage.removeItem("game");window.location.reload(!0)}function getDefaultGame(){return{startGame:!0,night:!0}}var game={};$(document).ready(function(){setInterval(saveGame,5e3);loadGame();setupButtons();refreshView()});